CREATE TABLE IF NOT EXISTS `#__jmparallax_slideshow_sliders` (`id` int(11) UNSIGNED NOT NULL AUTO_INCREMENT,`ordering` INT(11)  NOT NULL ,`state` TINYINT(1)  NOT NULL DEFAULT '1',`checked_out` INT(11)  NOT NULL ,`checked_out_time` DATETIME NOT NULL DEFAULT '0000-00-00 00:00:00',`created_by` INT(11)  NOT NULL ,`title` VARCHAR(255)  NOT NULL ,`data` LONGTEXT NOT NULL ,`setting` LONGTEXT NOT NULL ,PRIMARY KEY (`id`)) DEFAULT COLLATE=utf8_general_ci;CREATE TABLE IF NOT EXISTS `#__jmparallax_slideshow_category` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `ordering` int(11) NOT NULL,  `state` tinyint(1) NOT NULL,  `created_by` int(11) NOT NULL,  `parent_id` int(11) NOT NULL,  `title` varchar(255) NOT NULL,  `check_out` int(11) NOT NULL,  `check_out_time` datetime NOT NULL,  PRIMARY KEY (`id`)) DEFAULT COLLATE=utf8_general_ci;