<?php/** * @version     1.2.0 * @package     com_jm_deluxe_layer_slideshow * @copyright   Copyright (C) 2013 - joomlaman.com. * @license     http://www.gnu.org/copyleft/lgpl.html * @author      JoomlaMan <support@joomlaman.com> - http://joomlaman.com */// No direct accessdefined('_JEXEC') or die;jimport('joomla.application.component.view');/** * View to edit */class Jm_deluxe_layer_slideshowViewSlideshowslider extends JMasterViewDeluxeBaseJm {    /**     * Display the view     */    public function display($tpl = NULL) {        require_once JPATH_COMPONENT . '/helpers/jm_deluxe_layer_slideshow.php';        $id = $_POST['id'];        $data = Jm_deluxe_layer_slideshowHelper::getData($id);        $settings = json_decode(base64_decode(Jm_deluxe_layer_slideshowHelper::getSetting($id)));        $layers = json_decode(base64_decode($data));        $width = !empty($settings->width) ? $settings->width : 800;        $height = !empty($settings->height) ? $settings->height : 350;        $backgroundcolor = !empty($settings->backgroundcolor) ? $settings->backgroundcolor : "#FFF";        $fullwidth = !empty($settings->fullwidth) ? $settings->fullwidth : "off";        $html = Jm_deluxe_layer_slideshowHelper::showSlider($layers, $width, $height, $backgroundcolor, $fullwidth,true);        echo $html;    }}